const shareIconFactory = (tweetText, isAlt = false) => {
  const shareIcon = $(`<i></i>`, {
    class: 'fas fa-share-alt',
  });

  return $(`<a></a>`, {
    class: isAlt ? 'icon-wrapper alt' : 'icon-wrapper',
    href: `https://twitter.com/intent/tweet?text=${tweetText}&url=${this.location.href}`,
  }).append(shareIcon);
};

const locationPinIconFactory = () =>
  $(`<i></i>`, {
    class: 'fas fa-map-marker-alt',
  });

const dateIconFactory = () =>
  $(`<i></i>`, {
    class: 'far fa-calendar-alt',
  });

const toggleCards = (() => {
  let isHidden = false;

  return () =>
    isHidden ? $(`.grid-wrapper`).show() : $(`.grid-wrapper`).hide();
})();

const showOverlay = () => $(`.overlay`).show();
const hideOverlay = () => $(`.overlay`).hide();
const emptyOverlay = () => $(`.overlay`).empty();

const onClose = () => {
  hideOverlay();
  emptyOverlay();
};

const onApplyFactory = (isPremium) => {
  const regularApply = () => {
    emptyOverlay();
    return $(`.overlay`).append(applyConfirmationPopupFactory());
  };

  const premiumApply = () => {
    emptyOverlay();
    return $(`.overlay`).append(premiumWarningPopupFactory());
  };

  return isPremium ? premiumApply : regularApply;
};

const premiumWarningPopupFactory = () => {
  const header = $(`<div></div>`, {
    class: 'title-wrapper',
    text: `Premium Only Event`,
  });

  const content = $(`<div></div>`).append(
    $(`<div></div>`, {
      text: 'Sorry, this event is premium exclusive :/',
    }),
    $(`<div></div>`, {
      text:
        'You can check more about it clicking on the button below!',
    })
  );

  const premiumInfoButton = $(`<button></button>`, {
    class: 'default-button apply',
    click: onClose,
    text: `Read more`,
    width: `100%`,
  });

  const footer = $(`<div></div>`, {
    class: `details-footer-wrapper`,
  }).append(premiumInfoButton);

  const popupWrapper = $(`<div></div>`, {
    class: `popup-wrapper`,
  }).append(header, content, footer);
  return popupWrapper;
};

const applyConfirmationPopupFactory = () => {
  const header = $(`<div></div>`, {
    class: 'title-wrapper',
    text: `Success`,
  });

  const content = $(`<div></div>`, {}).append(
    `Great! Your application has been accepted! :)`
  );

  const timeoutBar = $(`<div></div>`, {
    class: `timeout-bar`,
  }).animate(
    {
      width: `100%`,
    },
    3000,
    `linear`,
    () => {
      emptyOverlay();
      hideOverlay();
    }
  );

  const popupWrapper = $(`<div></div>`, {
    class: `popup-wrapper`,
  }).append(header, content, timeoutBar);
  return popupWrapper;
};

function showEventDetails(event) {
  event.preventDefault();

  const eventCard = $(this);

  const isPremium = eventCard.attr(`id`) === `premium`;
  const eventTitle = eventCard.find(`.title-wrapper`).clone();
  const eventLocation = eventCard.find(`.location-wrapper`).clone();
  const eventDate = eventCard.find(`.date-wrapper`).clone();

  $(`.overlay`).append(
    eventDetailsPopupFactory(
      [eventTitle, eventLocation, eventDate],
      `Event description goes here!`,
      onApplyFactory(isPremium)
    )
  );
  showOverlay();
}

const eventDetailsPopupFactory = (
  headerProps,
  contentProps,
  onApply
) => {
  const header = $(`<div></div>`, {
    class: `details-header-wrapper`,
  }).append(...headerProps);

  const content = $(`<div></div>`, {
    class: `details-content-wrapper`,
  }).append(...contentProps);

  const applyButton = $(`<button></button>`, {
    class: 'default-button apply',
    click: onApply,
    text: `Apply`,
  });

  const closeButton = $(`<button></button>`, {
    class: 'default-button close',
    click: onClose,
    text: `Close`,
  });

  const footer = $(`<div></div>`, {
    class: `details-footer-wrapper`,
  }).append(closeButton, applyButton);

  const popupWrapper = $(`<div></div>`, {
    class: `popup-wrapper`,
  }).append(header, content, footer);
  return popupWrapper;
};

