const eventLabelFactory = (props, eventType) => {
  const textWrapper = $(`<div></div>`, {
    class: 'text-wrapper',
  }).append(...props);

  const labelWrapper = $(`<div></div>`, {
    class: `label-wrapper ${eventType}-label`,
  }).append(textWrapper);

  return labelWrapper;
};

const descriptionFactory = (title, location, date) => {
  const titleContainer = $(`<div></div>`, {
    class: 'title-wrapper',
  }).append(title);

  const locationContainer = $(`<div></div>`, {
    class: 'location-wrapper',
  }).append([locationPinIconFactory(), location]);

  const dateContainer = $(`<div></div>`, {
    class: 'date-wrapper',
  }).append([dateIconFactory(), date]);

  return $(`<div></div>`, {
    class: 'description-wrapper',
  }).append([titleContainer, locationContainer, dateContainer]);
};

const promotedCardFactory = (topProps, bottomProps, props) => {
  const topContent = $(`<div></div>`, { class: 'top' }).append(
    ...topProps
  );
  const bottomContent = $(`<div></div>`, { class: 'bottom' }).append(
    ...bottomProps
  );

  const contentWrapper = $(`<div></div>`, {
    class: 'content',
    click: showEventDetails,
  }).append(topContent, bottomContent);

  const cardWrapper = $(`<div></div>`, {
    class: 'card-wrapper promoted-card',
  }).append(...props, contentWrapper);

  $('.grid-wrapper').append(cardWrapper);
};

const regularCardFactory = (
  contentProps,
  props,
  isPremium = false
) => {
  const content = $(`<div></div>`, { class: 'bottom' }).append(
    ...contentProps
  );

  const contentWrapper = $(`<div></div>`, {
    id: isPremium ? `premium` : ``,
    class: 'content',
    click: showEventDetails,
  }).append(content);

  const cardWrapper = $(`<div></div>`, {
    class: 'card-wrapper regular-card',
  }).append(...props, contentWrapper);

  $('.grid-wrapper').append(cardWrapper);
};

